import React, {useState} from "react"
import axios from 'axios'

function App() {

	const [data, setData] = useState({})
	const [location, setLocation] = useState('')

	const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=10bc676f0f145e7d4af2bff51ed65f1c`

	const searchLocation = (event) => {
		if (event.key === 'Enter') {
			axios.get(url).then((response) => {
				setData(response.data)
				console.log(response.data)
		})
		setLocation('')
		}

	}

	return (
    <div className="App">
		<div className="search">
			<input 
			value={location}
			onChange={event => setLocation(event.target.value)}
			onKeyPress={searchLocation}
			placeholder = "Enter Location"
			type="text"/>
		</div>
      <div className="container">
		  <div className="top">
			<div className="location">
				<p>{data.name}</p>
			</div>
			<div className="temp">
				{data.main ? <h1>{data.main.temp}˚F</h1> : null}
				{/* <h1>{data.main.temp}˚F</h1> */}
			</div>
			<div className="description">
				{data.weather ? <p>{data.weather[0].main}</p> : null}
				{/* <p>{data.weather[0].main}</p> */}
			</div>
		  </div>
		  <div className="bottom">
			  <div className="feels">
				  {data.main ? <p className="bold">{data.main.feels_like}˚F</p> : null}
				  {/* <p className="bold">˚F</p> */}
				  <p>Feels Like</p>
			  </div>
			  <div className="humidity">
				 {data.main} <p className="bold">20%</p>
				  <p>Humidity</p>
			  </div>
			  <div className="wind">
				  <p className="bold">12MPH</p>
				  <p>Wind Speed</p>
			  </div>
		  </div>
	  </div>
    </div>
  );
}

export default App;
